  `siteMarked` varchar(5) NOT NULL,
  `patientAllergies` varchar(5) NOT NULL,
  `difficultAirway` varchar(5) NOT NULL,
  `riskBloodLoss` varchar(5) NOT NULL,
  `bloodLossUnits` varchar(25) NOT NULL,
  `anesthesiaSafety` varchar(5) NOT NULL,
  `allMembersTeam` varchar(5) NOT NULL,
  `introducationTeamMember` varchar(5) NOT NULL,
  `confirmIPPSC` varchar(5) NOT NULL,
  `siteMarkedAndVisible` varchar(5) NOT NULL,
  `relevantImages` varchar(5) NOT NULL,
