  `criticalStep` varchar(5) NOT NULL,
  `caseDuration` varchar(5) NOT NULL,
  `anticipatedBloodLoss` varchar(5) NOT NULL,
  `antibioticProphylaxis` varchar(5) NOT NULL,
  `anesthesiaAdditionalConcerns` varchar(5) NOT NULL,
  `sterilizationIndicators` varchar(5) NOT NULL,
  `nurseAdditionalConcerns` varchar(5) NOT NULL,
  `nameOperativeProcedure` varchar(5) NOT NULL,
  `specimensIdentified` varchar(5) NOT NULL,
  `anyEquipmentProblem` varchar(5) NOT NULL,
  `comments` text NOT NULL,
  `signNurse1Id` int(11) NOT NULL,
  `signNurse1FirstName` varchar(255) NOT NULL,
  `signNurse1MiddleName` varchar(255) NOT NULL,
  `signNurse1LastName` varchar(255) NOT NULL,
  `signNurse1FileStatus` varchar(255) NOT NULL,
  `signNurse1DateTime` datetime NOT NULL,
  `reliefNurse1` varchar(255) NOT NULL,
  `signNurse2Id` int(11) NOT NULL,
  `signNurse2FirstName` varchar(255) NOT NULL,
  `signNurse2MiddleName` varchar(255) NOT NULL,
  `signNurse2LastName` varchar(255) NOT NULL,
  `signNurse2FileStatus` varchar(255) NOT NULL,
  `signNurse2DateTime` datetime NOT NULL,
  `reliefNurse2` varchar(255) NOT NULL,
  `signNurse3Id` int(11) NOT NULL,
  `signNurse3FirstName` varchar(255) NOT NULL,
  `signNurse3MiddleName` varchar(255) NOT NULL,
  `signNurse3LastName` varchar(255) NOT NULL,
  `signNurse3FileStatus` varchar(255) NOT NULL,
  `signNurse3DateTime` datetime NOT NULL,
  `reliefNurse3` varchar(255) NOT NULL,
  `signNurse4Id` int(11) NOT NULL,
  `signNurse4FirstName` varchar(255) NOT NULL,
  `signNurse4MiddleName` varchar(255) NOT NULL,
  `signNurse4LastName` varchar(255) NOT NULL,
  `signNurse4FileStatus` varchar(255) NOT NULL,
  `signNurse4DateTime` datetime NOT NULL,
